public class problem15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, temp;

		a = 22;
		b = 11;

		System.out.println("Before swapping : a, b = " + a + ", " + b);

		temp = a;
		a = b;
		b = temp;
		System.out.println("After swapping : a, b = " + a + ", " + b);

	}

}

